mapper.select:
SELECT COUNT(*)
FROM rw_like
WHERE m_no = #{m_no}
  AND r_no = #{r_no}

mapper.insert:
INSERT INTO rw_like (
    rw_no,
    m_no,
    r_no
)
VALUES (
    rw_like_seq.NEXTVAL,
    #{m_no},
    #{r_no}
)

mapper.delete:
DELETE FROM rw_like
WHERE m_no = #{m_no}
  AND r_no = #{r_no}

mapper.select:
SELECT COUNT(*)
FROM rw_like
WHERE r_no = #{r_no}
